mapper.insert:
insert into menu(
	m_no,
	ma_code,
	mb_code,
	s_no,
	m_name,
	price,
	m_comment
)values(
	(select nvl(max(m_no),0)+1 from menu)
	,#{ma_code}
	,#{mb_code}
	,(select s_no from store where s_id=#{s_id})
	,#{m_name}
	,#{price}
	,#{m_comment}
)

mapper.update:
update menu 
set 
	ma_code=(select a.ma_code from code_menu_alpha a where a.ma_name=#{ma_code})
	,mb_code=(select b.mb_code from code_menu_beta b where b.mb_name=#{mb_code})
	,m_name=#{m_name}
	,price=#{price}
	,m_comment=#{m_comment}
where m_no=#{m_no}

mapper.insert:
insert into ingredient( 
	 i_no        
    ,ia_code     
    ,ib_code     
    ,io_code     
    ,s_no        
    ,num         
    ,i_name      
    ,i_size      
    ,price       
    ,i_comment   
  
  
)values (
	(select nvl(max(i_no),0)+1 from ingredient)
	,#{ia_code}
	,#{ib_code}
	,#{io_code}
	,#{s_no}
	,#{num}
	,#{i_name}
	,#{i_size}
	,#{price}
	,#{i_comment}
	
	
)

mapper.update:
update 
	ingredient set
	ia_code = #{ia_code}
	,ib_code	= #{ib_code}
	,io_code	= #{io_code}
	,s_no	= #{s_no}
	,num	= #{num}
	,i_name	= #{i_name}
	,i_size	= #{i_size}
	,price	= #{price}
	,i_comment	= #{i_comment}
where i_no = ${i_no}
