mapper.insert:
insert into ingredient( 
	 i_no        
    ,ia_code     
    ,ib_code     
    ,io_code     
    ,s_no        
    ,num         
    ,i_name      
    ,i_size      
    ,price       
    ,i_comment   
  
  
)values (
	(select nvl(max(i_no),0)+1 from ingredient)
	,#{ia_code}
	,#{ib_code}
	,#{io_code}
	,#{s_no}
	,#{num}
	,#{i_name}
	,#{i_size}
	,#{price}
	,#{i_comment}
	
	
)
